import React, { useState, useEffect } from "react";
import { toast } from "react-toastify";
import "react-toastify/dist/ReactToastify.css";
import { useAuth } from "../Store/authh";

const CreateTask = () => {
  const [questionText, setQuestionText] = useState("");
   // State to hold the retrieved userID
   const [userID, setUserID] = useState("");
  
  const {storeUserIDInLSGet} = useAuth();

 

  useEffect(() => {
    // Fetch userID from local storage when the component mounts
    const storedUserID = storeUserIDInLSGet();
    setUserID(storedUserID);
  }, [storeUserIDInLSGet]);

  const handleSubmit = async (e) => {
    e.preventDefault();

    const taskData = {
      questionText,
      userID,
    };

    try {
      const response = await fetch("api/authh/createtask", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify(taskData),
      });
      const result = await response.json();
        
      if (response.ok) {
        toast.success("Task created successfully!");
        setQuestionText("");
      
      } else {
        console.log(result.message);
        toast.error(result.message || "Failed to create task"); 
      
      }
    } catch (error) {
      console.error("Error creating task:", error);
     
    }
  };

  return (
    <div>
      <h2>Create a New Task</h2>
      <form onSubmit={handleSubmit}>
        <div>
          <label>Question Text:</label>
          <textarea
            value={questionText}
            onChange={(e) => setQuestionText(e.target.value)}
            required
          />
        </div>

        <button type="submit">Create Task</button>
      </form>
    </div>
  );
}

export default CreateTask;
