import React, { useState, useEffect } from "react";
import { toast } from "react-toastify";
import "react-toastify/dist/ReactToastify.css";
import { useAuth } from "../Store/authh";
import { useNavigate } from "react-router-dom";

const Timer = ({ time, onComplete }) => {
  const [seconds, setSeconds] = useState(time);

  useEffect(() => {
    if (seconds > 0) {
      const intervalId = setInterval(() => {
        setSeconds(seconds - 1);
      }, 1000);
      return () => clearInterval(intervalId);
    } else {
      onComplete();
    }
  }, [seconds, onComplete]);

  return <div>Time left: {seconds} seconds</div>;
};

const CreateTask = () => {
  const [questionText, setQuestionText] = useState("");
  const [userID, setUserID] = useState("");
  const [tasks, setTasks] = useState([]);
  const [showForm, setShowForm] = useState(false);
  const [currentTask, setCurrentTask] = useState(null);
  const [userAnswer, setUserAnswer] = useState("");
  const [mistakes, setMistakes] = useState(0);
  const navigate = useNavigate();

  const { UserIDFROMLSGet } = useAuth();

  useEffect(() => {
    const storedUserID = UserIDFROMLSGet();
    if (storedUserID) {
      setUserID(storedUserID);
    } else {
      console.error("No userID found in local storage.");
      toast.error("Failed to retrieve user ID. Please log in again.");
      navigate("/login");
    }
  }, [UserIDFROMLSGet, navigate]);

  useEffect(() => {
    if (userID) {
      fetchTasks();
    }
  }, [userID]);

  const fetchTasks = async () => {
    try {
      const response = await fetch(`/api/authh/alltaskget/${userID}`, {
        method: "GET",
        headers: {
          "Content-Type": "application/json",
        },
      });

      if (response.ok) {
        const fetchedTasks = await response.json();
        setTasks(fetchedTasks);
      } else {
        const result = await response.json();
        toast.error(result.message || "Failed to fetch tasks");
      }
    } catch (error) {
      toast.error("An error occurred while fetching tasks.");
    }
  };

  const startTask = (task) => {
    setCurrentTask(task);
    setShowForm(true);
    setUserAnswer("");
    setMistakes(0);
  };

  const handleTaskSubmit = async (status) => {
    // Implement the logic to submit the task with pass or fail status
    const taskData = {
      taskID: currentTask._id,
      userID,
      status,
      mistakes,
    };

    try {
      const response = await fetch("/api/authh/updateTaskStatus", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify(taskData),
      });

      if (response.ok) {
        toast.success("Task status updated successfully!");
      } else {
        const result = await response.json();
        toast.error(result.message || "Failed to update task status");
      }
    } catch (error) {
      toast.error("An error occurred while updating task status.");
    }

    setShowForm(false);
    setCurrentTask(null);
  };

  const handleAnswerChange = (e) => {
    const answer = e.target.value;
    setUserAnswer(answer);

    let mistakeCount = 0;
    const originalWords = currentTask.questionText.split(" ");
    const answerWords = answer.split(" ");

    for (let i = 0; i < originalWords.length; i++) {
      if (originalWords[i] !== answerWords[i]) {
        mistakeCount++;
      }
    }

    setMistakes(mistakeCount);
  };

  return (
    <div style={{ display: "flex", minHeight: "100vh", backgroundColor: "#f5f5f5" }}>
      <div
        style={{
          width: "25%",
          backgroundColor: "#2c3e50",
          padding: "20px",
          color: "#ecf0f1",
          overflowY: "auto",
        }}
      >
        <h2>Your Tasks</h2>
        {tasks.length > 0 ? (
          <ul style={{ listStyleType: "none", padding: 0 }}>
            {tasks.map((task) => (
              <li key={task._id}>
                <button
                  style={{
                    background: "#34495e",
                    border: "none",
                    padding: "10px",
                    textAlign: "left",
                    width: "100%",
                    cursor: "pointer",
                    marginBottom: "5px",
                    fontSize: "16px",
                    borderRadius: "5px",
                    color: "#ecf0f1",
                  }}
                  onClick={() => startTask(task)}
                  onMouseOver={(e) => {
                    e.currentTarget.style.backgroundColor = "#1abc9c";
                  }}
                  onMouseOut={(e) => {
                    e.currentTarget.style.backgroundColor = "#34495e";
                  }}
                >
                  {task.questionText}
                </button>
              </li>
            ))}
          </ul>
        ) : (
          <p>No tasks found.</p>
        )}
      </div>

      <div style={{ width: "75%", padding: "40px" }}>
        <div
          style={{
            backgroundColor: "#ecf0f1",
            padding: "30px",
            borderRadius: "10px",
            boxShadow: "0px 0px 10px rgba(0, 0, 0, 0.1)",
          }}
        >
          <h2>Create a New Task</h2>
          <button
            onClick={() => setShowForm(!showForm)}
            style={{
              backgroundColor: "#3498db",
              color: "#ecf0f1",
              padding: "10px 20px",
              border: "none",
              borderRadius: "5px",
              cursor: "pointer",
              fontSize: "16px",
              marginBottom: "20px",
            }}
            onMouseOver={(e) => {
              e.currentTarget.style.backgroundColor = "#2980b9";
            }}
            onMouseOut={(e) => {
              e.currentTarget.style.backgroundColor = "#3498db";
            }}
          >
            {showForm ? "Close Form" : "Add Task"}
          </button>

          {showForm && currentTask && (
            <>
              <Timer
                time={30}
                onComplete={() => handleTaskSubmit("fail")}
              />
              <form
                onSubmit={(e) => {
                  e.preventDefault();
                  handleTaskSubmit(mistakes === 0 ? "success" : "fail");
                }}
              >
                <div>
                  <label>Question: {currentTask.questionText}</label>
                  <textarea
                    value={userAnswer}
                    onChange={handleAnswerChange}
                    required
                    style={{
                      width: "100%",
                      padding: "10px",
                      borderRadius: "5px",
                      border: "1px solid #bdc3c7",
                      fontSize: "16px",
                      minHeight: "100px",
                    }}
                  />
                </div>
                <button
                  type="submit"
                  style={{
                    backgroundColor: "#3498db",
                    color: "#ecf0f1",
                    padding: "10px 20px",
                    border: "none",
                    borderRadius: "5px",
                    cursor: "pointer",
                    marginTop: "20px",
                    fontSize: "16px",
                  }}
                  onMouseOver={(e) => {
                    e.currentTarget.style.backgroundColor = "#2980b9";
                  }}
                  onMouseOut={(e) => {
                    e.currentTarget.style.backgroundColor = "#3498db";
                  }}
                >
                  Submit
                </button>
              </form>
            </>
          )}
        </div>
      </div>
    </div>
  );
};

export default CreateTask;
