import React, { useState, useEffect } from "react";
import { toast } from "react-toastify";
import "react-toastify/dist/ReactToastify.css";
import { useAuth } from "../Store/authh";
import { useNavigate } from "react-router-dom";

const Addtask = () => {
  const [questionText, setQuestionText] = useState("");
  const [userID, setUserID] = useState("");
  const [tasks, setTasks] = useState([]);
  const [showForm, setShowForm] = useState(false);
  const [currentTask, setCurrentTask] = useState(null);
  const [userAnswer, setUserAnswer] = useState("");
  const [timeLeft, setTimeLeft] = useState(null);
  const [mistakes, setMistakes] = useState(0);
  const [taskStarted, setTaskStarted] = useState(false);
  const navigate = useNavigate();

  const { UserIDFROMLSGet } = useAuth();

  useEffect(() => {
    const storedUserID = UserIDFROMLSGet();
    if (storedUserID) {
      setUserID(storedUserID);
    } else {
      console.error("No userID found in local storage.");
      toast.error("Failed to retrieve user ID. Please log in again.");
      navigate("/login");
    }
  }, [UserIDFROMLSGet, navigate]);

  useEffect(() => {
    if (userID) {
      fetchTasks();
    }
  }, [userID]);

  const fetchTasks = async () => {
    try {
      const response = await fetch(`/api/authh/alltaskget/${userID}`);
      if (response.ok) {
        const fetchedTasks = await response.json();
        setTasks(fetchedTasks);
      } else {
        const result = await response.json();
        toast.error(result.message || "Failed to fetch tasks");
      }
    } catch (error) {
      toast.error("An error occurred while fetching tasks.");
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    const taskData = { questionText, userID };

    try {
      const response = await fetch("/api/authh/createtask", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(taskData),
      });
      const result = await response.json();

      if (response.ok) {
        toast.success("Task created successfully!");
        setQuestionText("");
        setShowForm(false);
        fetchTasks();
      } else {
        toast.error(result.message || "Failed to create task");
      }
    } catch (error) {
      toast.error("An error occurred while creating the task.");
    }
  };

  const handleTaskClick = (task) => {
    if (task.status !== "Not Completed") {
      toast.error("Task is already completed.");
      return;
    }
    setCurrentTask(task);
    setUserAnswer(""); // Reset the answer field
    setMistakes(0); // Reset mistakes
    setTaskStarted(false); // Reset task started state
    setTimeLeft(null); // Reset the timer
  };

  const startTask = () => {
    if (currentTask) {
      setTimeLeft(30); // Start 30-second countdown
      setTaskStarted(true); // Indicate that the task has started
    }
  };

  useEffect(() => {
    if (timeLeft === null) return;

    if (timeLeft <= 0) {
      handleTaskUpdate();
      return;
    }

    const timer = setInterval(() => {
      setTimeLeft((prev) => prev - 1);
    }, 1000);

    return () => clearInterval(timer);
  }, [timeLeft]);

  const handleTaskUpdate = async () => {
    try {
      const mistakesCount = countMistakes();
      const status = mistakesCount === 0 ? "Passed" : "Failed";

      const response = await fetch(`/api/authh/updatetask/${currentTask._id}`, {
        method: "PUT",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({
          answer: userAnswer,
          mistakes: mistakesCount,
          status: status,
        }),
      });
      const result = await response.json();

      if (response.ok) {
        toast.success(`Task ${status.toLowerCase()} successfully!`);
        setCurrentTask(null);
        setUserAnswer("");
        setTimeLeft(null);
        fetchTasks();
      } else {
        toast.error(result.message || "Failed to update task");
      }
    } catch (error) {
      toast.error("An error occurred while updating the task.");
    }
  };

  const countMistakes = () => {
    if (!currentTask) return 0;
    const correctWords = currentTask.questionText.split(" ");
    const answerWords = userAnswer.split(" ");
    let mistakes = 0;

    for (let i = 0; i < correctWords.length; i++) {
      if (answerWords[i] !== correctWords[i]) mistakes++;
    }
    setMistakes(mistakes); // Update the mistakes state
    return mistakes;
  };

  // Call countMistakes whenever userAnswer changes
  useEffect(() => {
    countMistakes();
  }, [userAnswer]);

  return (
    <div style={{ display: "flex", minHeight: "100vh", backgroundColor: "#f5f5f5" }}>
      <div style={{ width: "25%", backgroundColor: "#2c3e50", padding: "20px", color: "#ecf0f1", overflowY: "auto" }}>
        <h2>Your Tasks</h2>
        {tasks.length > 0 ? (
          <ul style={{ listStyleType: "none", padding: 0 }}>
            {tasks.map((task) => (
              <li key={task._id}>
                <button
                  style={{
                    background: "#34495e",
                    border: "none",
                    padding: "10px",
                    textAlign: "left",
                    width: "100%",
                    cursor: "pointer",
                    marginBottom: "5px",
                    fontSize: "16px",
                    borderRadius: "5px",
                    color: "#ecf0f1",
                  }}
                  onMouseOver={(e) => e.currentTarget.style.backgroundColor = "#1abc9c"}
                  onMouseOut={(e) => e.currentTarget.style.backgroundColor = "#34495e"}
                  onClick={() => handleTaskClick(task)}
                >
                  {task.questionText}
                </button>
              </li>
            ))}
          </ul>
        ) : (
          <p>No tasks found.</p>
        )}
      </div>

      <div style={{ width: "75%", padding: "40px" }}>
        <div style={{ backgroundColor: "#ecf0f1", padding: "30px", borderRadius: "10px", boxShadow: "0px 0px 10px rgba(0, 0, 0, 0.1)" }}>
          <h2>Create a New Task</h2>
          <button
            onClick={() => setShowForm(!showForm)}
            style={{
              backgroundColor: "#3498db",
              color: "#ecf0f1",
              padding: "10px 20px",
              border: "none",
              borderRadius: "5px",
              cursor: "pointer",
              fontSize: "16px",
              marginBottom: "20px",
            }}
            onMouseOver={(e) => e.currentTarget.style.backgroundColor = "#2980b9"}
            onMouseOut={(e) => e.currentTarget.style.backgroundColor = "#3498db"}
          >
            {showForm ? "Close Form" : "Add Task"}
          </button>

          {showForm && (
            <form onSubmit={handleSubmit}>
              <div>
                <label>Question Text:</label>
                <textarea
                  value={questionText}
                  onChange={(e) => setQuestionText(e.target.value)}
                  required
                  style={{
                    width: "100%",
                    padding: "10px",
                    borderRadius: "5px",
                    border: "1px solid #bdc3c7",
                    fontSize: "16px",
                    minHeight: "100px",
                    boxSizing: "border-box",
                  }}
                />
              </div>
              <button
                type="submit"
                style={{
                  backgroundColor: "#3498db",
                  color: "#ecf0f1",
                  padding: "10px 20px",
                  border: "none",
                  borderRadius: "5px",
                  cursor: "pointer",
                  marginTop: "20px",
                  fontSize: "16px",
                }}
                onMouseOver={(e) => e.currentTarget.style.backgroundColor = "#2980b9"}
                onMouseOut={(e) => e.currentTarget.style.backgroundColor = "#3498db"}
              >
                Submit
              </button>
            </form>
          )}

          {currentTask && (
            <div style={{ marginTop: "20px" }}>
              <h3>Task: {currentTask.questionText}</h3>
              <div style={{ display: "flex", justifyContent: "space-between" }}>
                {timeLeft !== null && (
                  <div style={{ fontSize: "18px", fontWeight: "bold" }}>
                    Time Left: {timeLeft}s
                  </div>
                )}
                <div style={{ fontSize: "18px", fontWeight: "bold" }}>
                  Mistakes: {mistakes}
                </div>
              </div>
              {taskStarted ? (
                <div>
                  <textarea
                    value={userAnswer}
                    onChange={(e) => setUserAnswer(e.target.value)}
                    style={{
                      width: "100%",
                      padding: "10px",
                      borderRadius: "5px",
                      border: "1px solid #bdc3c7",
                      fontSize: "16px",
                      minHeight: "100px",
                      boxSizing: "border-box",
                    }}
                  />
                  <button
                    onClick={handleTaskUpdate}
                    style={{
                      backgroundColor: "#e74c3c",
                      color: "#ecf0f1",
                      padding: "10px 20px",
                      border: "none",
                      borderRadius: "5px",
                      cursor: "pointer",
                      marginTop: "20px",
                      fontSize: "16px",
                    }}
                    onMouseOver={(e) => e.currentTarget.style.backgroundColor = "#c0392b"}
                    onMouseOut={(e) => e.currentTarget.style.backgroundColor = "#e74c3c"}
                  >
                    Submit Answer
                  </button>
                </div>
              ) : (
                <button
                  onClick={startTask}
                  style={{
                    backgroundColor: "#27ae60",
                    color: "#ecf0f1",
                    padding: "10px 20px",
                    border: "none",
                    borderRadius: "5px",
                    cursor: "pointer",
                    marginTop: "20px",
                    fontSize: "16px",
                  }}
                  onMouseOver={(e) => e.currentTarget.style.backgroundColor = "#1e8449"}
                  onMouseOut={(e) => e.currentTarget.style.backgroundColor = "#27ae60"}
                >
                  Start Task
                </button>
              )}
            </div>
          )}
        </div>
      </div>
    </div>
  );
};

export default Addtask;
