import React, { useState, useEffect } from "react";
import { toast } from "react-toastify";
import "react-toastify/dist/ReactToastify.css";
import { useAuth } from "../Store/authh";
import { useNavigate } from "react-router-dom";

const CreateTask = () => {
  const [questionText, setQuestionText] = useState("");
  const [userID, setUserID] = useState("");
  const [tasks, setTasks] = useState([]);
  const [showForm, setShowForm] = useState(false);
  const [selectedTask, setSelectedTask] = useState(null); // Selected task for answering
  const [answer, setAnswer] = useState("");
  const [mistakes, setMistakes] = useState(0);
  const [timer, setTimer] = useState(30); // Timer countdown in seconds
  const navigate = useNavigate();

  const { UserIDFROMLSGet } = useAuth();

  useEffect(() => {
    const storedUserID = UserIDFROMLSGet();
    if (storedUserID) {
      setUserID(storedUserID);
    } else {
      console.error("No userID found in local storage.");
      toast.error("Failed to retrieve user ID. Please log in again.");
      navigate("/login");
    }
  }, [UserIDFROMLSGet, navigate]);

  useEffect(() => {
    if (userID) {
      fetchTasks();
    }
  }, [userID]);

  useEffect(() => {
    if (selectedTask && timer > 0) {
      const countdown = setInterval(() => {
        setTimer((prev) => prev - 1);
      }, 1000);
      return () => clearInterval(countdown);
    } else if (timer === 0) {
      submitTask("Failed");
    }
  }, [selectedTask, timer]);

  const fetchTasks = async () => {
    try {
      const response = await fetch(`/api/authh/alltaskget/${userID}`, {
        method: "GET",
        headers: {
          "Content-Type": "application/json",
        },
      });

      if (response.ok) {
        const fetchedTasks = await response.json();
        setTasks(fetchedTasks);
      } else {
        const result = await response.json();
        console.log(result.message);
        toast.error(result.message || "Failed to fetch tasks");
      }
    } catch (error) {
      console.error("Error fetching tasks:", error);
      toast.error("An error occurred while fetching.");
    }
  };

  const startTask = (task) => {
    setSelectedTask(task);
    setShowForm(false);
    setTimer(30); // Reset timer to 30 seconds
    setMistakes(0); // Reset mistakes counter
  };

  const handleAnswerChange = (e) => {
    const input = e.target.value;
    setAnswer(input);

    // Example mistake counting logic
    if (input.length > 0 && input[input.length - 1] !== selectedTask.answer[input.length - 1]) {
      setMistakes((prev) => prev + 1);
    }
  };

  const submitTask = async (status) => {
    try {
      const response = await fetch(`/api/authh/updatetask/${selectedTask._id}`, {
        method: "PUT",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify({ answer, mistakes, status }),
      });

      if (response.ok) {
        toast.success(`Task ${status === "Success" ? "completed" : "failed"}!`);
        setSelectedTask(null);
        setAnswer("");
        setTimer(30); // Reset timer
        fetchTasks(); // Fetch tasks after update
      } else {
        const result = await response.json();
        console.log(result.message);
        toast.error(result.message || "Failed to update task");
      }
    } catch (error) {
      console.error("Error updating task:", error);
      toast.error("An error occurred while updating the task.");
    }
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    const taskStatus = mistakes === 0 && timer > 0 ? "Success" : "Failed";
    submitTask(taskStatus);
  };

  return (
    <div style={{ display: "flex", minHeight: "100vh", backgroundColor: "#f5f5f5" }}>
      <div
        style={{
          width: "25%",
          backgroundColor: "#2c3e50",
          padding: "20px",
          color: "#ecf0f1",
          overflowY: "auto",
        }}
      >
        <h2>Your Tasks</h2>
        {tasks.length > 0 ? (
          <ul style={{ listStyleType: "none", padding: 0 }}>
            {tasks.map((task) => (
              <li key={task._id}>
                <button
                  style={{
                    background: "#34495e",
                    border: "none",
                    padding: "10px",
                    textAlign: "left",
                    width: "100%",
                    cursor: "pointer",
                    marginBottom: "5px",
                    fontSize: "16px",
                    borderRadius: "5px",
                    color: "#ecf0f1",
                  }}
                  onMouseOver={(e) => {
                    e.currentTarget.style.backgroundColor = "#1abc9c";
                  }}
                  onMouseOut={(e) => {
                    e.currentTarget.style.backgroundColor = "#34495e";
                  }}
                  onClick={() => startTask(task)}
                >
                  {task.questionText}
                </button>
              </li>
            ))}
          </ul>
        ) : (
          <p>No tasks found.</p>
        )}
      </div>

      <div style={{ width: "75%", padding: "40px" }}>
        <div
          style={{
            backgroundColor: "#ecf0f1",
            padding: "30px",
            borderRadius: "10px",
            boxShadow: "0px 0px 10px rgba(0, 0, 0, 0.1)",
          }}
        >
          {selectedTask ? (
            <form onSubmit={handleSubmit}>
              <h2>{selectedTask.questionText}</h2>
              <div>
                <label>Answer:</label>
                <input
                  type="text"
                  value={answer}
                  onChange={handleAnswerChange}
                  style={{
                    width: "100%",
                    padding: "10px",
                    borderRadius: "5px",
                    border: "1px solid #bdc3c7",
                    fontSize: "16px",
                  }}
                />
              </div>
              <p>Time remaining: {timer} seconds</p>
              <p>Mistakes: {mistakes}</p>
              <button
                type="submit"
                style={{
                  backgroundColor: "#3498db",
                  color: "#ecf0f1",
                  padding: "10px 20px",
                  border: "none",
                  borderRadius: "5px",
                  cursor: "pointer",
                  marginTop: "20px",
                  fontSize: "16px",
                }}
                onMouseOver={(e) => {
                  e.currentTarget.style.backgroundColor = "#2980b9";
                }}
                onMouseOut={(e) => {
                  e.currentTarget.style.backgroundColor = "#3498db";
                }}
              >
                Submit
              </button>
            </form>
          ) : (
            <>
              <h2>Create a New Task</h2>
              <button
                onClick={() => setShowForm(!showForm)}
                style={{
                  backgroundColor: "#3498db",
                  color: "#ecf0f1",
                  padding: "10px 20px",
                  border: "none",
                  borderRadius: "5px",
                  cursor: "pointer",
                  fontSize: "16px",
                  marginBottom: "20px",
                }}
                onMouseOver={(e) => {
                  e.currentTarget.style.backgroundColor = "#2980b9";
                }}
                onMouseOut={(e) => {
                  e.currentTarget.style.backgroundColor = "#3498db";
                }}
              >
                {showForm ? "Close Form" : "Add Task"}
              </button>

              {showForm && (
                <form onSubmit={handleSubmit}>
                  <div>
                    <label>Question Text:</label>
                    <textarea
                      value={questionText}
                      onChange={(e) => setQuestionText(e.target.value)}
                      required
                      style={{
                        width: "100%",
                        padding: "10px",
                        borderRadius: "5px",
                        border: "1px solid #bdc3c7",
                        fontSize: "16px",
                        minHeight: "100px",
                      }}
                    />
                  </div>
                  <button
                    type="submit"
                    style={{
                      backgroundColor: "#3498db",
                      color: "#ecf0f1",
                      padding: "10px 20px",
                      border: "none",
                      borderRadius: "5px",
                      cursor: "pointer",
                      marginTop: "20px",
                      fontSize: "16px",
                    }}
                    onMouseOver={(e) => {
                      e.currentTarget.style.backgroundColor = "#2980b9";
                    }}
                    onMouseOut={(e) => {
                      e.currentTarget.style.backgroundColor = "#3498db";
                    }}
                  >
                    Submit
                  </button>
                </form>
              )}
            </>
          )}
        </div>
      </div>
    </div>
  );
};

export default CreateTask;
