import React, { useState, useEffect } from "react";
import { toast } from "react-toastify";
import "react-toastify/dist/ReactToastify.css";
import { useAuth } from "../Store/authh";

const CreateTask = () => {
  const [questionText, setQuestionText] = useState("");
  const [userID, setUserID] = useState("");
  const [tasks, setTasks] = useState([]);

  const { UserIDFROMLSGet } = useAuth();

  useEffect(() => {
    const storedUserID = UserIDFROMLSGet();
    if (storedUserID) {
      setUserID(storedUserID);
    } else {
      console.error("No userID found in local storage.");
      toast.error("Failed to retrieve user ID. Please log in again.");
    }
  }, [UserIDFROMLSGet]);

  const handleSubmit = async (e) => {
    e.preventDefault();

    if (!userID) {
      toast.error("User ID is missing.");
      return;
    }

    const taskData = {
      questionText,
      userID,
    };

    try {
      const response = await fetch("/api/authh/createtask", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify(taskData),
      });
      const result = await response.json();

      if (response.ok) {
        toast.success("Task created successfully!");
        setQuestionText("");
        fetchTasks();
      } else {
        console.log(result.message);
        toast.error(result.message || "Failed to create task");
      }
    } catch (error) {
      console.error("Error creating task:", error);
      toast.error("An error occurred while creating the task.");
    }
  };

  const fetchTasks = async () => {
    if (!userID) {
      toast.error("User ID is missing.");
      return;
    }

    try {
      const response = await fetch(`/api/authh/alltaskget/${userID}`, {
        method: "GET",
        headers: {
          "Content-Type": "application/json",
        },
      });

      if (response.ok) {
        const fetchedTasks = await response.json();
        setTasks(fetchedTasks);
      } else {
        const result = await response.json();
        console.log(result.message);
        toast.error(result.message || "Failed to fetch tasks");
      }
    } catch (error) {
      console.error("Error fetching tasks:", error);
      toast.error("An error occurred while fetching the tasks.");
    }
  };

  useEffect(() => {
    fetchTasks();
  }, [userID]);

  return (
    <div style={{ display: "flex", minHeight: "100vh", backgroundColor: "#f5f5f5" }}>
      <div
        style={{
          width: "25%",
          backgroundColor: "#2c3e50",
          padding: "20px",
          color: "#ecf0f1",
          overflowY: "auto",
        }}
      >
        <h2>Your Tasks</h2>
        {tasks.length > 0 ? (
          <ul style={{ listStyleType: "none", padding: 0 }}>
            {tasks.map((task) => (
              <li key={task._id}>
                <button
                  style={{
                    background: "#34495e",
                    border: "none",
                    padding: "10px",
                    textAlign: "left",
                    width: "100%",
                    cursor: "pointer",
                    marginBottom: "5px",
                    fontSize: "16px",
                    borderRadius: "5px",
                    color: "#ecf0f1",
                  }}
                  onMouseOver={(e) => {
                    e.currentTarget.style.backgroundColor = "#1abc9c";
                  }}
                  onMouseOut={(e) => {
                    e.currentTarget.style.backgroundColor = "#34495e";
                  }}
                >
                  {task.questionText}
                </button>
              </li>
            ))}
          </ul>
        ) : (
          <p>No tasks found.</p>
        )}
      </div>

      <div style={{ width: "75%", padding: "40px" }}>
        <div
          style={{
            backgroundColor: "#ecf0f1",
            padding: "30px",
            borderRadius: "10px",
            boxShadow: "0px 0px 10px rgba(0, 0, 0, 0.1)",
          }}
        >
          <h2>Create a New Task</h2>
          <form onSubmit={handleSubmit}>
            <div>
              <label>Question Text:</label>
              <textarea
                value={questionText}
                onChange={(e) => setQuestionText(e.target.value)}
                required
                style={{
                  width: "100%",
                  padding: "10px",
                  borderRadius: "5px",
                  border: "1px solid #bdc3c7",
                  fontSize: "16px",
                  minHeight: "100px",
                }}
              />
            </div>
            <button
              type="submit"
              style={{
                backgroundColor: "#3498db",
                color: "#ecf0f1",
                padding: "10px 20px",
                border: "none",
                borderRadius: "5px",
                cursor: "pointer",
                marginTop: "20px",
                fontSize: "16px",
              }}
              onMouseOver={(e) => {
                e.currentTarget.style.backgroundColor = "#2980b9";
              }}
              onMouseOut={(e) => {
                e.currentTarget.style.backgroundColor = "#3498db";
              }}
            >
              Create Task
            </button>
          </form>
        </div>
      </div>
    </div>
  );
};

export default CreateTask;
